mapper.select:
select d.user_code as code, d.name, d.sex, d.phone, d.address, abs(l.amount) as amount, d.id_card, case when l.amount > 0 then 1 when l.amount < 0 then -1 else 0 end as loanType from (select user_id, sum(amount) as amount from loan n where 1 = 1
and n.status = #{status}
and n.loan_time > #{startTime}
and n.loan_time < #{endTime}
group by user_id) l left join directories d on l.user_id = d.id where d.status = 1
and d.name like concat('%', #{name},'%')
and l.amount > 0
and l.amount = 0
and l.amount < 0

select code, loan_type, abs(amount) as amount, loan_time, loan_channel, repayment_time, remark, is_iou
from loan where status = 1

    and code = #{code}


    and user_id = #{userId}

order by loan_time desc

mapper.update:
update loan
set repayment_time = #{repaymentTime},
    remark = #{remark},
    loan_channel = #{loanChannel},
    is_iou = #{isIou}
where code = #{code}
