mapper.select:
select l.code, d.name, d.sex, d.phone, d.address, l.loan_type,
       abs(l.amount) as amount, l.status, d.id_card,
       l.loan_channel
from loan l left join directories d on l.user_id = d.id
where d.status = 1

    and d.name = #{name}


    and l.loan_type = #{type}


    and l.status = #{status}


    and l.loan_time >= #{startTime}


    and l.loan_time <= #{endTime}

mapper.update:
update loan
set repayment_time = #{repaymentTime},
    remark = #{remark},
    loan_channel = #{loanChannel},
    is_iou = #{isIou}
where code = #{code}
